// Initialize MultiSlider with options
jQuery(document).ready(function($) {
    $('#hearderSlide').multislider({
        continuous: true,   // Enable continuous scrolling
        slideAll: false,    // Slide one at a time
        interval: 3000,     // Slide interval in milliseconds
        duration: 500,      // Animation duration in milliseconds
        hoverPause: true,   // Pause on hover
        pauseAbove: null,   // Pause on screen width above a value
        pauseBelow: null    // Pause on screen width below a value
    });
});
